text:
This page demonstrates various uses of a beat pad (grid of buttons) to demonstrate the game of life, cellular automata, chaotic attractors, and the Ising/Potts models.

P5 = require("p5")

function* createSketch(sketch) {
  const element = DOM.element('div');
  yield element;
  const instance = new P5(sketch, element, true);
  try {
    while (true) {
      yield element;
    }
  } finally {
    instance.remove();
  }
}

createSketch(s =&gt; {
  let cols = 8;
  let rows = 8;
  let buttonSize = 50;
  let buttons = [];

  s.setup = function() {
    s.createCanvas(cols * buttonSize, rows * buttonSize);
    for (let i = 0; i &lt; cols; i++) {
      buttons[i] = [];
      for (let j = 0; j &lt; rows; j++) {
        buttons[i][j] = new GridButton(i * buttonSize, j * buttonSize, buttonSize);
      }
    }
  };

  s.draw = function() {
    s.background(255);
    for (let i = 0; i &lt; cols; i++) {
      for (let j = 0; j &lt; rows; j++) {
        buttons[i][j].display();
      }
    }
  };

  s.mousePressed = function() {
    for (let i = 0; i &lt; cols; i++) {
      for (let j = 0; j &lt; rows; j++) {
        buttons[i][j].checkClick();
      }
    }
  };

  class GridButton {
    constructor(x, y, size) {
      this.x = x;
      this.y = y;
      this.size = size;
      this.color = s.color(255);
    }

    display() {
      s.fill(this.color);
      s.rect(this.x, this.y, this.size, this.size);
    }

    checkClick() {
      if (s.mouseX &gt; this.x && s.mouseX &lt; this.x + this.size &&
          s.mouseY &gt; this.y && s.mouseY &lt; this.y + this.size) {
        this.color = s.color(s.random(255), s.random(255), s.random(255));
      }
    }
  }
});
